import{_ as e,r as h,o as i,c as n,b as t,e as d,d as o,f as r}from"./app-af101884.js";const l={},c=r('<h1 id="vim" tabindex="-1"><a class="header-anchor" href="#vim" aria-hidden="true">#</a> Vim</h1><div class="hint-container tip"><p class="hint-container-title">tips</p><p>Vim 天下第一！！！</p></div><p>vim 是一个全屏幕纯文本编辑器，是 vi 编辑器的增强版</p><h2 id="一、vim-的工作模式" tabindex="-1"><a class="header-anchor" href="#一、vim-的工作模式" aria-hidden="true">#</a> 一、vim 的工作模式</h2><figure><img src="http://rocyan.oss-cn-hangzhou.aliyuncs.com/notes/yftjux.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><ul><li>命令模式：是主要使用快捷键的模式，命令模式想要进入输入模式，可以使用以下的方式</li></ul><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>a</td><td>在光标所在字符后插入</td></tr><tr><td>A</td><td>在光标所在行行尾插入</td></tr><tr><td>i</td><td>在光标所在字符前插入</td></tr><tr><td>I</td><td>在光标所在行行首插入</td></tr><tr><td>o</td><td>在光标下插入新行</td></tr><tr><td>O</td><td>在光标上插入新行</td></tr><tr><td>s</td><td>删除当前光标所在字符并进入插入模式</td></tr><tr><td>S</td><td>删除光标所在行并进入模式</td></tr><tr><td>c5</td><td>删除5个字符，并进入插入模式</td></tr><tr><td>C</td><td>删除光标所在位置至行尾的字符，并进入插入模式</td></tr></tbody></table><ul><li>输入模式：主要用于文本编辑</li><li>编辑模式（末行模式）：</li></ul><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>:q</td><td>退出 Vim，如果文件已被修改，将退出失败</td></tr><tr><td>:w</td><td>保存文件</td></tr><tr><td>:w 新文件名（绝对路径）</td><td>把文件另存为新文件</td></tr><tr><td>:wq</td><td>保存文件并退出 Vim</td></tr><tr><td>:q!</td><td>强制不保存退出</td></tr><tr><td>:wq!</td><td>强制保存退出，当文件的所有者或者root用户，对文件没有写权限的时候，强制写入数据使用</td></tr><tr><td>ZZ</td><td>退出 Vim，如果文件被改动过，保存改动内容</td></tr><tr><td>ZQ</td><td>与 :q! 相同，退出 Vim，不保存文件改动</td></tr></tbody></table><h2 id="二、命令模式-操作模式" tabindex="-1"><a class="header-anchor" href="#二、命令模式-操作模式" aria-hidden="true">#</a> 二、命令模式&amp;操作模式</h2><h3 id="_2-1-移动光标" tabindex="-1"><a class="header-anchor" href="#_2-1-移动光标" aria-hidden="true">#</a> 2.1 移动光标</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>h/j(ctrl+n)/k(ctrl+p)/l</td><td>左/下/上/右</td></tr><tr><td>0（数字0）</td><td>移动光标到本行开头</td></tr><tr><td>^</td><td>移动光标到本行第一个非空字符处</td></tr><tr><td>$</td><td>移动光标到行尾</td></tr><tr><td>w</td><td>向前移动一个词 （上一个字母和数字组成的词之后）</td></tr><tr><td>W</td><td>向前移动一个词 （以空格分隔的词）</td></tr><tr><td>5w</td><td>向前移动五个词</td></tr><tr><td>b</td><td>向后移动一个词 （下一个字母和数字组成的词之前）</td></tr><tr><td>B</td><td>向后移动一个词 （以空格分隔的词）</td></tr><tr><td>5B</td><td>向后移动五个词</td></tr><tr><td>gg</td><td>移动至文件开头</td></tr><tr><td>G</td><td>移动至文件末尾</td></tr><tr><td>:5</td><td>移动到第5行</td></tr><tr><td>(</td><td>跳转到上一句</td></tr><tr><td>)</td><td>跳转到下一句</td></tr><tr><td>{</td><td>跳转到上一段</td></tr><tr><td>}</td><td>跳转到下一段</td></tr><tr><td>[[</td><td>跳转到上一部分</td></tr><tr><td>]]</td><td>跳转到下一部分</td></tr><tr><td>[]</td><td>跳转到上一部分的末尾</td></tr><tr><td>][</td><td>跳转到上一部分的开头</td></tr></tbody></table><h3 id="_2-2-删除或剪切" tabindex="-1"><a class="header-anchor" href="#_2-2-删除或剪切" aria-hidden="true">#</a> 2.2 删除或剪切</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>x</td><td>剪切/删除单个字母</td></tr><tr><td>nx</td><td>剪切/删除n个字母</td></tr><tr><td>dd</td><td>剪切/删除单行</td></tr><tr><td>5dd</td><td>剪切/删除5行</td></tr><tr><td>dw</td><td>剪切/删除一个单词</td></tr><tr><td>dgg</td><td>剪切/删除至文件开头</td></tr><tr><td>dG</td><td>剪切/删除至文件结尾</td></tr><tr><td>:3,5d</td><td>删除第3行到第5行的内容</td></tr></tbody></table><h3 id="_2-3-复制粘贴" tabindex="-1"><a class="header-anchor" href="#_2-3-复制粘贴" aria-hidden="true">#</a> 2.3 复制粘贴</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>yy</td><td>复制单行</td></tr><tr><td>5yy</td><td>复制5行</td></tr><tr><td>p</td><td>粘贴到光标后</td></tr><tr><td>P（大）</td><td>粘贴到光标前</td></tr></tbody></table><h3 id="_2-4-撤销" tabindex="-1"><a class="header-anchor" href="#_2-4-撤销" aria-hidden="true">#</a> 2.4 撤销</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>u</td><td>撤销</td></tr><tr><td>ctrl+r</td><td>反撤销</td></tr></tbody></table><h3 id="_2-5-替换和查找" tabindex="-1"><a class="header-anchor" href="#_2-5-替换和查找" aria-hidden="true">#</a> 2.5 替换和查找</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>r</td><td>替换光标所在处的字符</td></tr><tr><td>R</td><td>从光标所在处开始替换字符，按ESC结束</td></tr><tr><td>/查找内容</td><td>从光标所在行向下查找</td></tr><tr><td>?查找内容</td><td>从光标所在行向上搜索</td></tr><tr><td>n</td><td>下一个</td></tr><tr><td>N</td><td>上一个</td></tr><tr><td>:1,10s/old/new/g</td><td>替换1到10行的所有old为new</td></tr><tr><td>:%s/old/new/g</td><td>替换整个文件的old为new</td></tr><tr><td>:%s/old/new/gc</td><td>替换整个文件的old为new，并在替换前先询问是否替换</td></tr></tbody></table><h3 id="_2-6-特殊插入" tabindex="-1"><a class="header-anchor" href="#_2-6-特殊插入" aria-hidden="true">#</a> 2.6 特殊插入</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>:r 文件名（绝对路径）</td><td>把文件内容导入光标位置</td></tr><tr><td>:r !命令</td><td>在vim中执行 系统命令 ，并把命令结果导入光标</td></tr></tbody></table><h3 id="_2-7-缩进" tabindex="-1"><a class="header-anchor" href="#_2-7-缩进" aria-hidden="true">#</a> 2.7 缩进</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>&gt;&gt;</td><td>增加一级缩进</td></tr><tr><td>&lt;&lt;</td><td>减少一级缩进</td></tr><tr><td>==</td><td>取消所有缩进</td></tr><tr><td>gg=G</td><td>全部格式化</td></tr><tr><td>:3,5&gt;</td><td>第3行到第5行增加一级缩进</td></tr><tr><td>:3,5&lt;</td><td>第3行到第5行减少一级缩进</td></tr><tr><td>:3,5=</td><td>格式化第3行到第5行</td></tr></tbody></table><h3 id="_2-8-vim-配置文件" tabindex="-1"><a class="header-anchor" href="#_2-8-vim-配置文件" aria-hidden="true">#</a> 2.8 vim 配置文件</h3>',25),b={href:"http://www.ruanyifeng.com/blog/2018/09/vimrc.html",target:"_blank",rel:"noopener noreferrer"},s=r('<h3 id="_2-9-其他" tabindex="-1"><a class="header-anchor" href="#_2-9-其他" aria-hidden="true">#</a> 2.9 其他</h3><figure><img src="http://rocyan.oss-cn-hangzhou.aliyuncs.com/notes/bsaccv.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="三、nerdtree插件快捷键" tabindex="-1"><a class="header-anchor" href="#三、nerdtree插件快捷键" aria-hidden="true">#</a> 三、NERDTree插件快捷键</h2><h3 id="_3-1-切换工作台和目录" tabindex="-1"><a class="header-anchor" href="#_3-1-切换工作台和目录" aria-hidden="true">#</a> 3.1 切换工作台和目录</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>ctrl + w + h</td><td>光标 focus 左侧树形目录</td></tr><tr><td>ctrl + w + l</td><td>光标 focus 右侧文件显示窗口</td></tr><tr><td>ctrl + w + w</td><td>光标自动在左右侧窗口切换</td></tr><tr><td>ctrl + w + r</td><td>移动当前窗口的布局位置</td></tr><tr><td>o</td><td>在已有窗口中打开文件、目录或书签，并跳到该窗口</td></tr><tr><td>go</td><td>在已有窗口中打开文件、目录或书签，但不跳到该窗口</td></tr><tr><td>t</td><td>在新 Tab 中打开选中文件/书签，并跳到新 Tab</td></tr><tr><td>T</td><td>在新 Tab 中打开选中文件/书签，但不跳到新 Tab</td></tr><tr><td>i</td><td>横向split 一个新窗口打开选中文件，并跳到该窗口</td></tr><tr><td>gi</td><td>横向split 一个新窗口打开选中文件，但不跳到该窗口</td></tr><tr><td>s</td><td>纵向split 一个新窗口打开选中文件，并跳到该窗口</td></tr><tr><td>gs</td><td>纵向split 一个新 窗口打开选中文件，但不跳到该窗口</td></tr><tr><td>o</td><td>打开选中的结点下</td></tr><tr><td>O</td><td>递归打开选中的结点下的所有目录</td></tr><tr><td>x</td><td>合拢选中结点的父目录</td></tr><tr><td>X</td><td>递归合拢选中结点下的所有目录</td></tr><tr><td>I</td><td>切换是否显示隐藏文件</td></tr><tr><td>r</td><td>递归刷新选中目录</td></tr><tr><td>R</td><td>递归刷新根结点</td></tr></tbody></table><h3 id="_3-2-切换标签页" tabindex="-1"><a class="header-anchor" href="#_3-2-切换标签页" aria-hidden="true">#</a> 3.2 切换标签页</h3><table><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td>:tabc</td><td>关闭当前的 tab</td></tr><tr><td>:tabo</td><td>关闭所有其他的 tab</td></tr><tr><td>:tabs</td><td>查看所有打开的 tab</td></tr><tr><td>:tabp</td><td>前一个 tab</td></tr><tr><td>:tabn</td><td>后一个 tab</td></tr><tr><td>gT</td><td>前一个 tab</td></tr><tr><td>gt</td><td>后一个 tab</td></tr></tbody></table>',7);function _(u,f){const a=h("ExternalLinkIcon");return i(),n("div",null,[c,t("p",null,[d("配置文件地址 ~/.vimrc，详细配置参考"),t("a",b,[d("阮一峰老师的 Vim 配置入门"),o(a)])]),s])}const m=e(l,[["render",_],["__file","linux-vim.html.vue"]]);export{m as default};
